#define M_PI 3.14159265358979323846

// raymarching function for the marble
float s(vec3 p){
  float d=length(p),a=atan(p.x,p.z),b=atan(p.x,p.y),patt=0.5+0.5*sin(10.0*a)*sin(10.0*b);
  return d-=1.0+0.05*patt;
}

// main function
void mainImage(out vec4 f,in vec2 c){
  float aspect=iResolution.x/iResolution.y;
  vec2 uv=c/iResolution.xy;
  uv-=0.5;uv.x*=aspect;
  float d=1.0;
  vec3 p=vec3(0,0,-4),dp=normalize(vec3(uv,1));
  vec3 c1=vec3(0.5,0.7,1.0);
  for(int i=0;i<64;i++){
    d=s(p);
    if(d<0.001){
      vec3 n=vec3(s(p+vec3(0.001,0,0))-s(p-vec3(0.001,0,0)),s(p+vec3(0,0.001,0))-s(p-vec3(0,0.001,0)),s(p+vec3(0,0,0.001))-s(p-vec3(0,0,0.001)));
      float l=dot(normalize(vec3(1,0.5,1)),normalize(n));
      c1=mix(vec3(0.5,0.4,0.3),vec3(1,1,1),l);
      break;
    }
    p+=dp*d;
  }
  f=vec4(c1,1.0);
}
