void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 sum = vec3(0.0);
    vec2 offs = vec2(1.0 / iResolution.x, 1.0 / iResolution.y);

    sum += texture(iChannel0, uv + offs * vec2(-2, -2)).rgb * 0.0044299121055113265;
    sum += texture(iChannel0, uv + offs * vec2(-1, -2)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2( 0, -2)).rgb * 0.0215963866053;
    sum += texture(iChannel0, uv + offs * vec2( 1, -2)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2( 2, -2)).rgb * 0.0044299121055113265;

    sum += texture(iChannel0, uv + offs * vec2(-2, -1)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2(-1, -1)).rgb * 0.0189897948556;
    sum += texture(iChannel0, uv + offs * vec2( 0, -1)).rgb * 0.04200594931922;
    sum += texture(iChannel0, uv + offs * vec2( 1, -1)).rgb * 0.0189897948556;
    sum += texture(iChannel0, uv + offs * vec2( 2, -1)).rgb * 0.00895781211794;

    sum += texture(iChannel0, uv + offs * vec2(-2,  0)).rgb * 0.0215963866053;
    sum += texture(iChannel0, uv + offs * vec2(-1,  0)).rgb * 0.04200594931922;
    sum += texture(iChannel0, uv + offs * vec2( 0,  0)).rgb * 0.0944703978506;
    sum += texture(iChannel0, uv + offs * vec2( 1,  0)).rgb * 0.04200594931922;
    sum += texture(iChannel0, uv + offs * vec2( 2,  0)).rgb * 0.0215963866053;

    sum += texture(iChannel0, uv + offs * vec2(-2,  1)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2(-1,  1)).rgb * 0.0189897948556;
    sum += texture(iChannel0, uv + offs * vec2( 0,  1)).rgb * 0.04200594931922;
    sum += texture(iChannel0, uv + offs * vec2( 1, 1)).rgb * 0.0189897948556;
    sum += texture(iChannel0, uv + offs * vec2( 2, 1)).rgb * 0.00895781211794;

    sum += texture(iChannel0, uv + offs * vec2(-2,  2)).rgb * 0.0044299121055113265;
    sum += texture(iChannel0, uv + offs * vec2(-1,  2)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2( 0,  2)).rgb * 0.0215963866053;
    sum += texture(iChannel0, uv + offs * vec2( 1,  2)).rgb * 0.00895781211794;
    sum += texture(iChannel0, uv + offs * vec2( 2,  2)).rgb * 0.0044299121055113265;

    fragColor = vec4(sum, 1.0);

}
