void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv = sphereProject(uv);

    vec4 sampl = texture(iChannel0, uv);
    fragColor = vec4(sampl.rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 c1 = texture(iChannel0, uv);
    vec4 c2 = texture(iChannel1, uv);

    //float ratio = clamp((iMouse.x / iResolution.x) * 1.5, 0.0, 1.0);
    float ratio = sin(iTime * 2.0) + 1.0;
    float w = 0.0;
    float rgbLength = length(c1.rgb); 


    fragColor = mix(c1, c2, smoothstep(ratio - w, ratio, rgbLength));
}
