#define pi 3.14159265

vec3 color(float speed, float offset)
{
    float r = pi/2.0 + offset;
    float g = 7.0*pi/6.0 + offset;
    float b = 11.0*pi/6.0 + offset;

    return abs(sin(vec3(r, g, b)));

}

float circle(vec2 plane, vec2 position, float radius)
{
    return length(plane-position) - radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0*(fragCoord-0.5*iResolution.xy-0.5)/iResolution.y;
    float a = 0.125*iTime;
    mat2 r45 = mat2(cos(a), -sin(a), sin(a), cos(a));
    uv *= r45;
    vec3 col = vec3(0.0);

    vec2 gv = (fract(uv*5.0))-0.5;
    float circles = 0.0; 

    float t = 2.0*iTime;
    vec2 id = floor(5.0*uv);

    for(float y = -1.0; y <= 1.0; y++)
    {
        for(float x = -1.0; x <= 1.0; x++)
        {
            vec2 offset = vec2(x,y);
            float dist = 0.3*length(id+offset+0.5);
            float r = mix(0.3, 1.5, 0.5*sin(t+dist)+0.5);

            circles += smoothstep(r, r*0.99, length(gv-offset));
        }
     }

    col += mod(circles, 2.0)* color(1.0, iTime+length(gv+uv));


    fragColor = vec4(col,1.0);
}
