float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx,vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;


    vec4 col = vec4(0.);

    vec4 c1 = texture(iChannel0, uv);





    float ma = max(c1.x, max(c1.y, c1.z));
    float mi = min(c1.x, min(c1.y, c1.z));


    col = vec4(ma - mi);


    fragColor = col;
}
