vec3 gungi( vec2 co )
{
	vec3 a = fract( cos( co.x*1. + co.y )*vec3(1.3, 4.7, 2.9) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e4, 0.1e4) );
	vec3 c = mix(a, b, 1.0);
	return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2. * fragCoord.xy / iResolution.xy;
	vec2 uvs = uv + iResolution.xy / max(iResolution.x, iResolution.y);

	vec3 p = vec3(uvs / 10., 0);


	p +=  vec3(sin(iTime / 72.), sin(iTime *00.022),  sin(iTime * 728.));



	vec2 seed = p.xy * 2.0;	
	seed = floor(seed * iResolution.x);
	vec3 rnd = gungi( seed );

	vec4 starcolor = vec4(pow(rnd.x,40.0));



	fragColor = starcolor+0.11;
}
