void mainImage( out vec4 O, in vec2 px )
{
    O = vec4(0);

    // Upscale tex with blur
    for (int i = -1; i <= 1; i++)
    for (int j = -1; j <= 1; j++) 
        O += texelFetch(iChannel0, ivec2(0.5 * px) + ivec2(i,j), 0);

    O /= 9.;

    // Black bars for full immersion
    vec2 res = iResolution.xy;
    O *= step(2., 0.5 * res.y - abs(px.y - 0.5 * res.y));
}

#define pi 3.14159
#define t iTime

void mainImage( out vec4 O, in vec2 px )
{
    vec2 res = iResolution.xy;

    // Pixelate px
    float v = 1.5;
    px = v * floor(px / v);

    // Downscale px
    px *= 2.;

    vec2 uv = (px - 0.5 * res) / res.y;
    float k = 4. / res.y;

    // Points
    vec2 p = vec2(-0.4, 0.2);
    vec2 q = -p;

    // Angles
    float ap = atan(uv.y - p.y, uv.x - p.x);
    float aq = atan(uv.y - q.y, uv.x - q.x);

    // Circles
    float dp = length(uv - p);
    float dq = length(uv - q);
    float scp = step(0.13, dp);
    float scq = step(0.13, dq);

    // Strips
    float sc = 5. / pi;   
    float iap = floor(sc * ap + 0.23 * t);
    float iaq = floor(sc * aq - 0.1 * t);
    float fap = fract(sc * ap + 0.23 * t) - 0.5;
    float faq = fract(sc * aq - 0.1 * t) - 0.5;
    float sp = smoothstep(-k, k, 0.25-abs(fap));
    float sq = smoothstep(-k, k, 0.25-abs(faq));

    // Color
    vec3 col = vec3(0);
    col = mix(col, vec3(1,1,0), sp * mod(iap, 2.));  
    col = mix(col, vec3(1,0,0), sq * mod(iaq, 2.));
    col = mix(col, vec3(1,1,0), sp * mod(iap + 1., 2.));
    col = mix(col, vec3(1,0,0), sq * mod(iaq + 1., 2.));
    col = mix(col, vec3(1,1,0), 1.-scp);
    col = mix(col, vec3(1,0,0), 1.-scq);

    // Wanted AA to mix with white instead of background,
    // this also hurts to look at though so is good :)
    if ((sp>0.&&sp<1. || sq>0.&&sq<1.) && length(col) < 0.1)
        col = vec3(1);

    O = vec4(col, 1);
}
