void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 startingPoint = vec2(0.0, 0.5);
    vec2 coneDirection = vec2(1.0, 0.0);
    const float PI = 3.141592;

    float initialWidth = 0.01;
    float startingHeightRatio = 0.5; //the fraction of the vertical height of the screen to start from
    float coneAngle = PI / (4. * sin(2. * iTime) + 13.); //this is an angle with the horizontal

    float coneRange = uv.x * tan(coneAngle) + initialWidth; //use the definition of tangent

    if (uv.y > startingHeightRatio - coneRange && uv.y < startingHeightRatio + coneRange)
    {
    vec3 col = vec3(1.,1.,0.1 * sin(3.* uv.x -  15. * iTime) + 0.5);

    fragColor = vec4(col, 1.0);
    }

}
