void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
    vec4 base = texture(iChannel0, uv);
    vec3 video = texture(iChannel1, uv).rgb;

    vec3 blendclr = vec3(0.);
    blendclr.rgb = mix(video.rrr, base.rgb, base.a);

    vec4 hardMixColor = vec4(0.0);
    hardMixColor.rgb = step(0.73,  blendclr.rgb); // apply hard mix


    // Output to screen

    vec3 col = vec3(0.1,0.8,0.2)*hardMixColor.rgb;
    fragColor = vec4(col, hardMixColor.a);
}

float inner(vec2 size, vec2 uv){

    size = 0.25-size*0.25;
    vec2 v = step(size,uv *(1.-uv));

    float result = v.x*v.y;

    return result ;
}


float innerrect(vec2 maxnum,vec2 newuv,float blur, float innerblur){



    vec2 offset = vec2(0.3,0.0);

    float mv = inner(vec2(0.05,0.7), newuv)*0.8;   
    float lv = inner(vec2(0.05,0.7), newuv+offset)*0.4;  
    float rv = inner(vec2(0.05,0.7), newuv-offset)*0.2;   

    return (lv+rv+mv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(0.);

	uv *= 60.;
    uv = fract(uv);

    vec2 newuv = uv;
    uv = uv*2.-1.;

    float rect = innerrect(vec2(1.),newuv, 0.01, 0.2);
    color = vec3(rect);

    fragColor = vec4(color,rect);
}
