mat3 rst(float r, float f, vec2 t)
{
    float c = cos(r);
    float s = sin(r);
    return mat3( c*f, s  , t.x , -s , c*f, t.y , 0 ,  0 ,  1  );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float t = iTime/5.0;
    float s = sin(t);
    float c = cos(t);

    float rot = radians(0.0);
    float scl = 1.0;
    vec2  pos = vec2(0.3, 0.0);

    mat3 m = rst(rot, scl, pos);
    vec3 uv3 = vec3(uv,1.0);

#if 1
    uv3 *= m;       // wtf? why yes?
#else
    uv3 = m * uv3;  // wtf? why not?
#endif

    uv = uv3.xy;
    fragColor = vec4(texture(iChannel0,uv + 0.5));
}
