renderpass.code:
float square(vec2 p, float size)
{
    p /= size;
    vec2 q = mod(p, vec2(1.0));
    if (q.x < 0.5 && q.y < 0.5) {
        return 1.0;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    float size = 0.1;
    float size2 = 0.11;

    float RV = 1.0;  // radians per second
    float RV2 = 1.0+0.1*sin(iTime*0.05); // radians per second for second grid

    float beatThreshold = 0.5;
    float pulseAmount = 1.75;
    float pulseTime = 0.1;
    float pulseWidth = 0.1;
    float angle = iTime * RV;
    float angle2 = iTime * RV2;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    mat2 rot2 = mat2(cos(angle2), -sin(angle2), sin(angle2), cos(angle2));
    vec3 color = vec3(0.0);
    vec2 q = vec2(p.x, p.y);
    vec2 q2 = vec2(p.x+0.1*cos(iTime*0.1), p.y+0.1*sin(iTime*0.1));

    q = rot * q;
    q2 = rot2 * q2;
    q = mod(q, vec2(size, size));
    q2 = mod(q2, vec2(size2, size2));

	float freqs[6];
	//Sound
	freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.03, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.05, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[4] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[5] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;


    float beat = 0.25*freqs[0]*freqs[0]
                +0.25*freqs[1]*freqs[1]
                +0.25*freqs[2]*freqs[2]
                +0.25*freqs[3]*freqs[3];
    beat = sqrt(beat);

    float highs = sqrt(0.5*(freqs[4]*freqs[4]+freqs[5]*freqs[5]));

    size *= mix(1.2, pulseAmount, beat);
    size2 *= mix(1.1, pulseAmount, highs);

    if (square(q, size) > 0.5) {
        color = vec3(0.6, 0.6, 0.6);
    }

    if (square(q2, size2) > 0.5) {
        if (square(q, size) > 0.5) {
            color = vec3(1.0);
        } else {
            color = vec3(0.6, 0.6, 0.6);
        }
    }
    fragColor = vec4(color+beat/2.5, 1.0);
}
