void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);
    if(uv.x > iMouse.x / iResolution.x){
        col = texture(iChannel0, uv).rgb;
    }else{
        col = texture(iChannel1, uv).rgb;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

float Rand( vec2 n )
{
    return sin(dot(n, vec2(1233.224, 1743.335)));
}

float frac(float v)
{
    return v - floor(v);
}

float _Iteration = 10.0;
float _BlurRadius = 0.003;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec2 randomOffset = vec2(0.0, 0.0);
    vec3 col = vec3(0.0);
    float random = Rand(uv);

    for(int k = 0; k < int(_Iteration); k++){
        random = frac(43758.5453 * random + 0.61432);
        randomOffset.x = (random - 0.5) * 2.0;
        random = frac(43758.5453 * random + 0.61432);
        randomOffset.y = (random - 0.5) * 2.0;
        col += texture(iChannel0, uv+vec2(randomOffset.x*_BlurRadius, randomOffset.y*_BlurRadius)).rgb;
    }
    col = col / _Iteration;

    //vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
