#define aa 2./iResolution.y

float visualizeCircleOutline(vec2 p, vec2 point, float radius) {

    float length = length(point-p);
    float space = fract(length*20.)*0.1;
    return min(smoothstep(radius-aa, radius+aa, length+aa),smoothstep(radius+aa, radius-aa, length))+space;
}

vec2 pow2(vec2 v, float p){
    return vec2(pow(v.x,p),pow(v.y,p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = (fragCoord - iResolution.xy*.5)/iResolution.y; // normalize space
    vec2 p = u + clamp(pow2(u,2.)*10.,0.,1.) - 1.;

    float mask = step(0.5,length(u*2.));

    u = mix(p,u,mask);

    float circle = visualizeCircleOutline(u, vec2(sin(iTime/4.*2.),cos(iTime/4.)), .7);

    float v = circle;

    // Output to screen
    fragColor = vec4(vec3(circle),1.0);
}
