vec3 bgCol = vec3(1.0, 1.0, 1.0);
float shadowRadius = 0.6;
float circleRadius = 0.5;
vec3 circleColor = vec3(1.0, 0.5, 0.5);
vec3 sphereSpec = vec3(1.0, 1.0, 1.0);

//Main method/function
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p = uv * 2.3 - 1.0;
	p.x *= iResolution.x / iResolution.y;
	vec3 col = bgCol;
	float f = smoothstep(shadowRadius * 0.8, shadowRadius, length(p));
	col -= mix(col, vec3(0.0), f) * 0.2;

    float radius = sqrt(dot(p, p));
    if(radius < circleRadius) {
		vec3 bg = col;
		col = circleColor;
		f = smoothstep(circleRadius - 0.01, circleRadius, radius);
		col = mix(col, bg, f);
	}	

	fragColor = vec4(col, 1);
}
