vec3 palette( float t ) {
  vec3 a = vec3(0.108, 0.500, 0.448);
  vec3 b = vec3(-0.752, 0.588, -0.362);
  vec3 c = vec3(-0.782, 0.838, 0.778);
  vec3 d = vec3(-0.192, -0.300, 0.528);

  return a + b*cos( 6.28318*(c*t+d) );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord * 6.0 - iResolution.xy) / iResolution.y;

   vec2 uv0 = uv;

   vec3 finalColor = vec3(0.0);

   for (float i = 0.0; i < 3.0; i++ ) {
   uv = fract(uv * 4.0) - 1.5;

   float d = length(uv);

   vec3 col = palette(length(uv0) + iTime*.6);

   d = sin(d*8. + iTime)/8.;
   d = abs(d);

   d = 0.02 / d;

   finalColor += col * d;
   }


   uv = fract(uv * 2.0) - 0.5;

   float d = length(uv);

   vec3 col = palette(length(uv0) + iTime);

   d = sin(d*8. + iTime)/8.;
   d = abs(d);

   d = 0.02 / d;

   finalColor += col * d;

   fragColor = vec4(finalColor, 1.0);

}
