void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0; // Normalize pixel coordinates

    // Set up firework properties
    vec2 fireworkPos = vec2(0.0, -0.5);
    float fireworkRadius = 0.05;
    float fireworkExplosionRadius = 0.1;
    float fireworkSpeed = 0.5;
    float explosionSpeed = 0.5;
    float explosionDuration = 1.0;
    float explosionStartTime = 0.5;
    vec3 fireworkColor = vec3(1.0, 1.0, 1.0);
    vec3 explosionColor = vec3(1.0, 1.0, 0.0);

    // Calculate distance to firework center
    float fireworkDist = length(p - fireworkPos);

    // Check if firework should explode
    if (fireworkDist < fireworkRadius) {
        // Calculate explosion time
        float explosionTime = (iTime - explosionStartTime) * explosionSpeed;
        float explosionProgress = 1.0 - smoothstep(0.0, explosionDuration, explosionTime);

        // Calculate explosion radius
        float explosionRadius = mix(fireworkExplosionRadius, 0.0, explosionProgress);

        // Calculate explosion color
        vec3 color = mix(fireworkColor, explosionColor, explosionProgress);

        // Calculate distance to explosion center
        float explosionDist = length(p - fireworkPos);

        // Check if pixel is within explosion radius
        if (explosionDist < explosionRadius) {
            // Set pixel color
            fragColor = vec4(color, 1.0);
        } else {
            // Set pixel to black
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        }
    } else {
        // Move firework up
        fireworkPos.y += fireworkSpeed * iTime;

        // Set pixel to black
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
