const int i_max = 2055;
vec2 c = vec2( -0.12256, 0.74486);
float er2 = 4.0; // 
vec2 complex_square( vec2 v ) {
	return vec2(
		v.x * v.x - v.y * v.y,
		v.x * v.y * 2.0
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 z = fragCoord.xy - iResolution.xy * 0.5;
	z *= 2.5 / min( iResolution.x, iResolution.y );




	float scale = 0.1;

	int count = 0;


	for ( int i = 0 ; i < i_max; i++ ) {

		z = c + complex_square( z );
		count = i;
        if ( dot(z,z) > er2 ) {  break; }
	}


    if (count == i_max-1) {fragColor = vec4(0.0, 6.0,1.0,2.0);} 
    else  fragColor = vec4(1.0- float( count ) * scale ); 
}
