/**
 * Draw a rectangle pos with width/height/color
 * The position is topleft in pixels
 */
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;
	if (uv.x >= pos.x && (uv.x < pos.x + width)
		&& (iResolution.y - uv.y) >= pos.y && ((iResolution.y - uv.y) < pos.y + height)) {
		t = 1.0;
	}
	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	vec2 pos = vec2(.0,.0);
	float width = 0.5 * iResolution.x;
	float height = 0.5 * iResolution.x;

	// colors
    vec4 background = vec4(0.365,0.227,0.227,1.0);
    vec3 color = vec3(0.71,0.878,0.729);

    // rect
	vec4 rect = rectangle(uv, pos, width, height, color);

	// mix background & rect
	fragColor = mix(background, rect, rect.a);
}
