vec3 oval(vec2 uv, vec2 center, vec2 size) {
    vec2 dist = (uv - center) / size;
    float edge = smoothstep(1.0, 0.95, length(dist));
    return vec3(1.0 - edge);
}

vec3 addPattern(vec3 egg, vec2 uv, vec2 center, vec2 size) {
    vec2 dist = (uv - center) / size;
    float pattern = sin(dist.y * 12.0 + dist.x * 3.0) * 0.5 + 0.5;
    return egg * mix(vec3(1.0), vec3(0.24, 0.8, 0.9), pattern);
}

vec3 createBackground(vec2 uv) {
    float gradient = uv.y;
    return mix(vec3(0.3, 0.35, 0.45), vec3(0.2, 0.45, 0.55), gradient);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 background = createBackground(uv);

    float t = sin(iTime * 0.5) * 0.5 + 0.5;


    vec3 egg1 = oval(uv, vec2(0.35, t), vec2(0.15, 0.35)) * vec3(0.7, 0.4, 0.6);
    vec3 egg2 = oval(uv, vec2(0.55, 1.0 - t), vec2(0.1, 0.25)) * vec3(0.5, 0.8, 0.6);
    vec3 egg3 = oval(uv, vec2(0.5, 0.5), vec2(0.5, 0.5)) * vec3(0.6, 0.6, 0.9);


    egg1 = addPattern(egg1, uv, vec2(0.7, t), vec2(0.15, 0.25));
    egg2 = addPattern(egg2, uv, vec2(0.6, 1.0 - t), vec2(0.1, 0.2));
    egg3 = addPattern(egg3, uv, vec2(0.5, 0.5), vec2(0.5, 0.5));

    vec3 col = background + egg1 + egg2 + egg3;

    fragColor = vec4(col, 1.0);
}
