void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord/iResolution.xy) - vec2(0.5,0.5)) * 2.;
    uv.x *= iResolution.x / iResolution.y;

    float rotations = 94.23;
    float sharpness = 50.;

    float value = distance(uv*vec2(0.5), vec2(0.0, 0.0));

    value = pow(value, 0.5)*0.5;
    value += pow(value, 2.);

    // Left side of the screen needs to add 0.5, in order to form a full circle
    float add = step(0.5,(uv.x * -1.) + 0.5)*0.5;
    float rotation = clamp((atan(uv.x, uv.y)/(6.28)),0.,1.) + clamp(((atan(-uv.x, -uv.y)/(6.28)) ),0.0,1.0) + add;

    // Animate rotation
    rotation += iTime*0.02;

    float radialGlows = pow((sin(rotation*rotations)+1.0)/2., 1.);

    float arches = abs(sin((rotation*rotations*0.5)-0.8));

    float radialWires = pow(( abs(sin(value*200.+(arches*2.5)) + 1.0) / 2.0) ,sharpness);

    float beams = pow(radialGlows, sharpness*8.);

    // Add the radial wires and the beams
    value = radialWires + beams;


    // Add moving light
    vec2 lightUv = uv;
    lightUv.y -= 0.2;
    lightUv.x += sin(iTime*0.5);
    float spotlight = 1.-distance(lightUv*vec2(0.3), vec2(0.0, 0.0));
    spotlight *= 1.2;
    value = value * pow(spotlight,3.0);

    vec3 blueColor = vec3(0.5,0.1,1.0);
    vec3 whiteColor = vec3(0.9, 0.9, 1.0);
    vec3 mixColor = mix(blueColor, whiteColor, value);

    fragColor = vec4(mixColor * value,1.0);
}
