vec3 burst(vec2 st, vec2 pos, float r, vec3 col)
{
    st -= pos;
    r =   r * r;
    return clamp((r / dot(st, st)), 0.,1.)  * clamp(col, 0.,1.) ;
}


float blink2(float f) {
    f = f / 10.;
    float PI = 3.1415926;
    return .5*( 1.+sin(f*PI));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = fragCoord/iResolution.y;
    float t = blink2( iTime );
    vec3 col = vec3(.0,.0,.0);
    vec3 ttt =  burst(uv,vec2(0.5,0.5),0.25,vec3(0.4+t,0.5+t,0.5+t));
    col = max(col,ttt);
    ttt  =  -burst(uv,vec2(blink2( iTime ),0.5),0.15,vec3(0.4+t,0.5+t,0.5+t));
    col +=ttt;


    //col = max(col,ttt);
    fragColor = vec4(col,1.0);
}
