vec3 Circle(vec2 uv, vec2 position, float radius, float blur, vec3 col)
{
    float aspect_ratio = iResolution.x / iResolution.y;
    vec2 corrected_uv = vec2(uv.x*aspect_ratio, uv.y);
    vec2 corrected_position = vec2(position.x * aspect_ratio, position.y);
    vec2 distance = corrected_uv - corrected_position;
    float length_of_distance = length(distance);
    float shape = smoothstep(radius, radius-blur, length_of_distance);
    return col * shape;
}

vec3 ClampC(vec3 c)
{
    return vec3(clamp(c, vec3(0.,0.,0.), vec3(1.,1.,1.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 result = vec3(0.0);

    vec3 face = Circle(uv, vec2(0.5,0.5), 0.3, 0.03, vec3(1.0,0.,0.0));

    vec3 inner_face = Circle(uv, vec2(0.5,0.5), 0.2, 0.03, vec3(1.,1.,1.));
    vec3 smile_circle = Circle(uv, vec2(0.5,0.55), 0.2, 0.03, vec3(1.,0.0,0.));

    vec3 eye_hole = Circle(uv, vec2(0.5,0.6), 0.1, 0.03, vec3(cos(0.5 * iTime),1.0,1.0));
    vec3 cornea = Circle(uv, vec2(0.5,0.6), 0.05, 0.01, vec3(cos(1.0 * iTime * 2.),0.,0.));
    vec3 iris_hole = Circle(uv, vec2(0.5,0.6), 0.025, 0.01, vec3(1.0,1.0,1.0));
    vec3 iris = Circle(uv, vec2(0.5,0.6), 0.025, 0.02, vec3(cos(1.0 * iTime * 20.),0.0,0.0));

    result = ClampC(result + face);
    result = ClampC(result - inner_face);
    result = ClampC(result + smile_circle);
    result = ClampC(result - eye_hole);
    result = ClampC(result + cornea);
    result = ClampC(result - iris_hole);
    result = ClampC(result + iris);


    fragColor = vec4(vec3(result), 1.0);
}
