#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.141592
vec2 heart(float s,vec2 offset, vec2 st){
	float sign=(st.x>offset.x)?(1.):(-1.);
    float cor=(st.y>offset.y+.1)?(0.):(-PI);
    float x=sign*asin(pow((sign*(st.x-offset.x)/s/16.),1./3.))+cor;
	float y=s*(13.*cos(x)-5.*cos(2.*x)-2.*cos(3.*x)-cos(4.*x))+offset.y;
    return vec2(st.x,y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec2 offset=vec2(0.);
	vec2 p = heart(0.02,offset,st);
    float plot=smoothstep(2./iResolution.y,0.,length(p-st));
    vec3 color = mix(vec3(0.),vec3(1.),plot);
    fragColor = vec4(color,1.0);
}
