#ifdef GL_ES
precision mediump float;
#endif
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.y,center,m;
    st=fract(st*7.);
	float plot = 0.,r=0.1+abs(0.05*sin(iTime)),f=fract(iTime/5.),sign1,sign2;
    vec3 color=vec3(1.);
    for(int i=-1; i<4; i++){
        for(int j=-1; j<4; j++){
            sign1=(mod(float(j)+2.,2.)==0.)?1.:-1.;
            sign2=(mod(float(i)+2.,2.)==0.)?-1.:1.;
            if(f<0.5){m=vec2(sign1*smoothstep(0.,1.,f),0.);}
            else{m=vec2(0.,sign2*smoothstep(0.,1.,(f-.5)));}
            center=vec2(0.5*float(i),0.5*float(j))+m;
            plot=smoothstep(0.,7./iResolution.y,r-length(st-center));
            color = mix(color,vec3(0.),plot);
        }
    }
    fragColor = vec4(color,1.0);
}
