float hash(vec2 a, float seed){
    seed += 2.;
    a += 2.;
    a.x *= (1000. * sin(seed) * a.x  +  1000.*sin(seed + 1.)) * (1000. * sin(seed + 2.) * a.y  +  1000.*sin(seed + 3.)) / 9000.;
    a.y *= (1000. * sin(seed + 4.) * a.x  +  1000.*sin(seed + 5.)) * (1000. * sin(seed + 6.) * a.y  +  1000.*sin(seed + 7.)) / 9000.;

    a.x = mod(a.x,1.1);
    a.y = mod(a.y,1.1);

    return fract(a.x + a.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(hash(uv,iTime));
    // Output to screen
    fragColor = vec4(col,1.0);
}
