void mainImage( out vec4 O, vec2 I )
{
    for(float i,h=1e2; i++<h; I.y += sin((I.x*i+iTime*h*i)/h + tan(i+I.y/7e1))/i/.1)
        O = .5+.5*cos(ceil(I.y/h)+vec4(3,2,0,0));
}

