void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.xy; 

    //MOVING COLORS AROUND WITH MOUSE POSITION
    //let's try to have mouse position drive a change in the uv space by 
    //somehow combining mouse position with the uv value.

    //get the normalized mouse position value.
    vec2 mouseValue = iMouse.xy/iResolution.xy;
    //subtract the mousevalue from the uv value.
    //i'm also going to use fract() to force the value to 'loop around' back to 1 once it dips beneath 0.
    fragColor = vec4(fract(st.x - mouseValue.x), fract(st.y - mouseValue.y), 0.0, 1.0);
}
