void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    p.x *= iResolution.x / iResolution.y;
    p.x -= 0.5;

    float r = 0.2;
    float re = 0.0;
    float ct = (0.5 - r - re)*cos(iTime) + 0.5;
    float st = (0.5 - r - re)*sin(iTime) + 0.5;


    vec2 q = p - vec2(ct,st);

    // idfk this was an accident but looks cool;
    q.x *= p.x *= iResolution.x / iResolution.y;
    p.x *= iResolution.x / iResolution.y;

    // background
    vec3 col = vec3(1.0);

    // sphere
    col *= smoothstep(r, r + re, length(q));

    // Output to screen
    fragColor = vec4(col,1.0);
}
