vec3 palette( float t ){
    vec3 dc = vec3(0.748, 0.358, 0.698);
    vec3 amp = vec3(0.218, 0.218, -0.172);
    vec3 frq = vec3(1.000, 0.638, 1.000);
    vec3 phs = vec3(0.057, 0.390, 0.723);

    return dc + amp*cos( 6.28318*(frq*t*phs)); }
    //gradient

float noise(float z){
    return abs((sin(z*40.)+2.*sin(z*20.)+3.*sin(z*13.33)+4.*sin(z*10.)+5.*sin(z*.8)+6.*sin(z*.66)+7.*sin(z*.57))/25.)-.3;
    }  //funkcja o ciekawym kształcie złożona z sinusoid



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv=(fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 uv0=uv;

    //float MyTime = iTime + -abs(sin(iTime/2.)/5.-5.);
    float MyTime = iTime/4. + (sin(iTime/4.5)*4.);  //manipulacja czasem

    vec3 finalColor = vec3(1.);

    for(float i;i<2.;i++){

    //uv = sin(uv*1.5)*2.+.4;
    //uv = max(min(sin(uv*4.)+cos(uv*4.)+tan(uv*1.5+1.)/30.,1.5),-1.5);
    //uv = max(min(sin(uv*3.)+cos(uv*3.)+tan(uv+1.)/30.,1.5),-1.5);
    uv = max(min(sin(uv*3.+sin(MyTime/4.)/4.)+cos(uv*3.-sin(MyTime/4.)/4.)+tan(uv+1.+tan(MyTime/8.))/30.,1.5),-1.5);

    float d = length(uv);
    d -= MyTime/4.;
    float pat_circle = noise(d);  //wzorek kulisty


    vec3 pat_flash = vec3((-tan(MyTime*4.)/3.+0.3));  //wzorek migający
    pat_flash += vec3((-tan(MyTime*4.+.5)/8.+0.3));  //drugie mignięcie
    pat_flash *= palette(MyTime/4.+d);

    vec3 pat_sine = vec3(sin(MyTime/1.5)/2.5+.5);  //wzorek sinusoida
    pat_sine *= noise(d)+1.;

    pat_flash *= pat_circle;


    finalColor *= abs(pat_sine);
    finalColor += pat_flash;


    fragColor = vec4(finalColor,1.);

    }

    }
