void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 nfragCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspect_ratio = (iResolution.x / iResolution.y); 
    nfragCoord.x *= aspect_ratio;

    float wave_r = texture(iChannel0, vec2(nfragCoord.x / (aspect_ratio * 2.0) + 0.50, 0.75)).x / 2.0;
    float wave_g = texture(iChannel0, vec2(nfragCoord.x / (aspect_ratio * 2.0) + 0.52, 0.75)).x / 2.0;
    float wave_b = texture(iChannel0, vec2(nfragCoord.x / (aspect_ratio * 2.0) + 0.54, 0.75)).x / 2.0;

    vec3 col = vec3(0.0);
    nfragCoord.y /= aspect_ratio;
    nfragCoord.y += 0.25;
    col.r += 1.0 - smoothstep(-0.1, 0.01 + nfragCoord.y / 4.0, abs(wave_r - nfragCoord.y));
    col.g += 1.0 - smoothstep(0.0, 0.01, abs(wave_g - nfragCoord.y));
    col.b += 1.0 - smoothstep(0.0, 0.01, abs(wave_b - nfragCoord.y));

    col = vec3(col.r * 2.0, col.g * 2.0, col.b * 2.0);

    fragColor = vec4(vec3(col / 2.0), 1.0);
}
