void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(texture(iChannel0, uv).rgb,1.0);
}

const vec2 mousePixel = vec2(0.5, 0.5);

float distanceToSegment( vec2 a, vec2 b, vec2 p )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (mousePixel == fragCoord) {
        fragColor = iMouse;
        return;
    }

    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb * 0.95;

    if (iMouse.z > 0. && iMouse.w < 0.) {
        vec4 prevMouse = texture(iChannel0, mousePixel-0.5);
        if (prevMouse.z > 0.) {
            float d = distanceToSegment(iMouse.xy, prevMouse.xy, fragCoord);
            if (d < 3.) {
                col += 1.;
            }
        }
    }

    col = min(col, 1.);

    fragColor = vec4(col,1.0);
}
