float f_n_rand(float n){
    return fract(sin(n*323.23)*33123.123 + 32.23*sin(32.23*n));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trpino_nooffset = (fragCoord.xy)/ iResolution.xy;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.xy;

    vec2 o_scale = vec2(0.02+o_trmono_nooffset);
    vec2 o_fragCoord_scaled = ((fragCoord.xy)*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);

    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);
    vec2 o_trpino_scldfloor = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;

    float n_index = floor(o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x);
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);


    vec2 o_scale_rand = vec2(f_n_rand(o_fragCoord_scaled_floor.y*iTime)*0.02+0.004);
    vec2 o_fragCoord_scaled_rand = ((fragCoord.xy)*o_scale_rand);
    vec2 o_iResolution_scaled_rand = (iResolution.xy * o_scale_rand);

    vec2 o_fragCoord_scaled_floor_rand = floor(o_fragCoord_scaled_rand);
    vec2 o_iResolution_scaled_floor_rand = floor(o_iResolution_scaled_rand);
    vec2 o_fragCoord_scaled_fract_rand = fract(o_fragCoord_scaled_rand);
    vec2 o_iResolution_scaled_fract_rand = fract(o_iResolution_scaled_rand);


    vec2 o_offset = vec2(
        (f_n_rand(iTime*o_fragCoord_scaled_floor_rand.y)-0.5)*(0.02+o_trmono_nooffset.x),
        0.
    );
    vec4 o_col = texture(iChannel0, o_trpino_nooffset);
    vec4 o_col_offset = texture(iChannel0, o_trpino_nooffset+(o_offset));
    vec4 o_col_offset2 = texture(iChannel0, o_trpino_nooffset-(o_offset));
    fragColor = o_col_offset;
    if(o_trpino.x > -.5){
        fragColor = vec4(
            o_col_offset.r, 
            o_col.g, 
            o_col.b, 
            1.
        );
        if(o_trpino.x - -0.5 < 0.01){
            fragColor = vec4(0.);
        }
    }
    if(o_trpino.x > 0.0){
        fragColor = vec4(
            o_col.r, 
            o_col_offset.g, 
            o_col_offset2.b, 
            1.
        );
        if(o_trpino.x - 0. < 0.01){
            fragColor = vec4(0.);
        }
    }
    if(o_trpino.x > 0.5){
        fragColor = o_col;
        if(o_trpino.x - 0.5 < 0.01){
            fragColor = vec4(0.);
        }

    }


}
