void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv * 2.0 - 1.0;

    vec3 col = vec3(0.0, 0.0, 0.0);

    // create the rainbow shape
    float rainbow = 1.0 - length(p);

    // add color
    if (rainbow > 0.0) {
        col = mix(vec3(1.0, 0.0, 0.0), vec3(1.0, 0.5, 0.0), rainbow);
        col = mix(col, vec3(1.0, 1.0, 0.0), rainbow);
        col = mix(col, vec3(0.0, 1.0, 0.0), rainbow); // add the green color
        col = mix(col, vec3(0.0, 1.0, 1.0), rainbow);
        col = mix(col, vec3(0.0, 0.0, 1.0), rainbow);
        col = mix(col, vec3(1.0, 0.0, 1.0), rainbow);
    }

    fragColor = vec4(col, 1.0);
}
