float curveOut(float t) {
  return pow(t, 4.0);
}
float lineSlant(  vec2 canvas, vec2 pos, float height, float lean, float thinkness, float border, float f)
{
    vec2 rel = pos-canvas;
    float heightPct = rel.y / height;
    rel.x += lean * heightPct;

    float xborder = border + abs(lean)*0.01 * f * step(0.0001, border);
    float xthinkness = thinkness + (abs(lean)*0.04 * f);

    float p = step(xthinkness*-0.5,rel.x-xborder) * step(rel.x, (xthinkness*0.5)-xborder);
    float h = (height*0.5);
    p *= step(-h,rel.y-border) * step(rel.y, h-border);

    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;
    uv += vec2((iResolution.x / iResolution.y) *-0.5,-0.5);

    float t = iTime*3.0;
    float xx = (0.5 + sin(iTime*2.0)*0.5);
    uv *= xx * xx;
    float height = 0.75 * max(0.20,curveOut(xx) );
    float lean = height * 0.75 * curveOut(xx);
    float stroke = 0.031  ;

    vec2 p = vec2(0.0);

    float think_inner = lineSlant(uv, p, height, lean, stroke*4.0, stroke, 1.0);
    float think_outter = lineSlant(uv, p, height, lean, stroke*4.0, 0.0, 1.0);
    float thin = lineSlant(uv, p, height, -lean, stroke+0.01, 0.0,0.33);

    vec3 col = vec3(0.0);
    col = mix(col, vec3(1.0), thin);
    col = mix(col, vec3(1.0), think_outter);
    col = mix(col, vec3(0.0), think_inner);


    fragColor = vec4(col, 1.0);
}
