#define PI 3.1415926
#define s(a) sin(a)
#define c(a) cos(a)
#define t iTime
void mainImage(out vec4 O, in vec2 I)
{
    vec2  uv=I/iResolution.xy;
    float x=uv.x,
          y=uv.y,
          f=(s((x)*PI*2.+y*PI*s(c(t*.7-y)+s(y-t)*9.)*.2-PI/2.)-y)*(y*PI+.3);
    O=vec4(f,f-y,f*.5-y*sin(x*PI*2.+t),1.);
}
