vec3 pallete (float t ) {
vec3 a = vec3(0.508,0.108,0.618);
vec3 b = vec3(-0.312,0.868,-0.762);
vec3 c = vec3(2.38,1.5,1.2);
vec3 d = vec3(0.42,1.2,-0.5);
return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
vec2 uv0 = uv;
vec3 finalColor = vec3(0.0);
for (float i = 0.0; i < 3.75; i ++) {
uv = fract(uv / 1.0) - 0.5;
   float d = length(uv)* exp(+length(uv0));
   vec3 col = pallete(length(uv0)+ i*6.7 - iTime*.5);
   d = sin(d*3.5 + iTime)/10.;
   d = abs(d);
   d = smoothstep(0.0,0.15,d);
   d = .05 / d;
   finalColor += col * d;
   }
   fragColor = vec4(finalColor,1.0);
}
