vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

#define triwave_(p) abs(fract(.5+p/4.0)-.5)*2.
#define triwave1_(p) (abs(fract(p/8.0)-.5)-abs(fract(p/2.0)-.5)/2.)
float t1_(vec2 c, vec2 p){
    return triwave_((.5 - length( min(p=fract(p*sign(triwave1_(c*c.y))), 1.-p.yx) )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = .5;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    float time1 = iTime/64.;
    uv += vec2(time1/2.0,time1/3.0)/t1/4.0+(vec2(cos(time1),sin(time1)))*8./t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float multiplier = 2.;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    for(int k = 0; k < 6; k++){

        //uv.y -= t1_(floor(uv),floor(uv));

        uv = (uv-t2/scale)/scale;
        //if(uv.x<uv.y) uv = uv.yx;
        t2 = triangle_wave(uv-.5);
        //if(t2.x>t2.y) t2 = t2.yx;
        uv = -(t2+triangle_wave(uv.yx));
        //if(uv.x<uv.y) uv.y += .5;
        col.x = min(length(uv)-col.x,col.x*(multiplier));
        col = abs(col.yzx-vec3(1.-col.x))/multiplier;
    }
    fragColor =
        vec4(col*multiplier,1.0);
}

float fmod(float a, float b){
    return mod(floor(a),b);
}

