float random(vec2 st) {
    return fract(sin(dot(st,vec2(397.523,591.5939)))*53426.79);
}

float noise(vec2 st) {
    vec2 ip = floor(st);
    vec2 fp = fract(st);

    float a = random(ip);
    float b = random(ip + vec2(1., 0.));
    float c = random(ip + vec2(0., 1.));
    float d = random(ip + vec2(1., 1.));

    vec2 u = fp * fp * (3.0 - 2.0 * fp);
    return mix(mix(a, b, u.x), mix(c, d, u.x), u.y) * 2. - 1.;
}

float fractalBrownMotion(vec2 uv) {
    float n = abs(noise(vec2(uv.x, noise(uv) * 30. + iTime)));
    return pow(1. - n, 3.) * 1.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(fractalBrownMotion(uv*6.)) * vec3(noise(uv) + .8, .9, .9);

    fragColor = vec4(col,1.);
}
