#define SOUND_MULTIPLIER 3.0
#define PI acos(-1.)

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    // Calculate polar coordinates
    float r = length(uv);
    vec3 bg = vec3(0., 0., 0.2) * (1. - length(uv));

    // Draw the lines
    const float it = 5.0;
    float c = 0.0;
    for( float i = 0.0 ; i < it ; i += 1.0 ) {
        float i01 = i / it;
        float rnd = pow(texture( iChannel0, vec2(i01)).x, 1.);
        float react = SOUND_MULTIPLIER * texture( iChannel1, vec2(sqrt(i01), 0.0) ).x;    

        float c1 = (.5 + react) * 0.002 * abs( 1.0 / sin( (uv.y +0.) +
                                                         sin(uv.x * 15.0 * (i01+.3) * rnd + rnd * 7.0 + iTime * .5 * (i01*3. + .2)) *
                                                                 (0. + 0.15*react) * sin(PI * fragCoord.x / iResolution.x) * (i01*2.+.2)) );
        c = clamp(c1, 0.0, 1.0);
        vec3 col = mix(vec3(0., .6, 2.), vec3(0., 2., 0.), sqrt(i01));
        bg = mix(bg, col, c);
    }


    float s = 0.0;
    const float it2 = 20.0;

    for( float i = 0.0 ; i < it2 ; i += 1.0 ) {
        float i01 = i / it2;       
        float react = SOUND_MULTIPLIER * texture( iChannel1, vec2(i01, 0.0) ).x;  
        vec2 rnd = texture( iChannel0, vec2(i01)).xy;
        vec2 rnd2 = rnd - 0.5;

        rnd2 = vec2(0.85*sin(rnd2.x * 200.0 + rnd2.y * iTime * 0.1), 
                    -0.1 - 0.15 * sin(rnd2.x * rnd2.x * 200.0 + iTime  * rnd2.x * 0.25));

        float r1 = 1.0 - length(uv - rnd2);
        float rad = ( 1.0 - clamp(0.03 * rnd.y + react * 0.05, 0.0, 1.0) );

        r1 = smoothstep(rad, rad + 0.015, r1);
    }


    fragColor = vec4(bg, 1.0);
}
