// reference:https://www.shadertoy.com/view/4lc3Wn

const float MAX_E = log(48.0);
const float TAU = 6.28318;
const float PI = 3.14159;

// hsb2rgb.
vec3 hsb2rgb(float h, float s, float b){
  vec3 c = vec3(h, s, b);
  vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
  rgb = rgb * rgb * (3.0 - 2.0 * rgb);
  return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 z )
{
    z = 2.0 * z/iResolution.xy - 1.0;
    z.x *= iResolution.x/iResolution.y;

    float r = length(z);
    r = pow(r, 0.25); // trick.

    float t = (atan(z.y, z.x) + PI) / TAU;

    float t0 = t - fract(iTime*0.125);
    float t1 = t + fract(iTime*0.125);

    float value0 = (t0 + r)*10.0;
    float value1 = (t1 - r)*10.0;

    float c0 = fract(value0);
    float n0 = floor(value0);
    float g0 = mod(n0, 2.0);
    n0 = mod(n0, 10.0) / 10.0;

    float c1 = fract(value1);
    float n1 = floor(value1);
    float g1 = mod(n1, 2.0);
    n1 = mod(n1, 10.0)/10.0;

    float c2 = 1.57 * (c0*2.0-1.0);
    float c3 = 1.57 * (c1*2.0-1.0);

    vec4 tex = texture(iChannel0, vec2(n0, n1));
	float phase = TAU * tex.r;
	float expandVelocity = PI + 2.0 * tex.g - 1.0;
    float m = exp(0.25*(1.0 - cos(iTime*expandVelocity + phase)) * MAX_E);
    float alpha = pow(1.0-cos(c2)*cos(c3), m * 0.5);

    vec3 baseColor = hsb2rgb(fract(iTime*0.05), length(z)*0.707, pow(length(z)*0.707, 0.5));
    vec4 col = vec4(baseColor * alpha, 1.0);
    fragColor = col;
}
