#define rot(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage( out vec4 o, vec2 u) { 
    o *= 0.;
    vec2 R = iResolution.xy;
    u = (u - R / 2.) / R.y;
    float s, c, i, a, h, d = 1., t = iTime; 

    while(i++ < 120. && d > .1) {
        vec3 p = vec3( a * u, a - 11.);

        p.yz *= rot(t);
        p.y *= .8;
        p *= .4;
        h = 9. / dot(p, p);
        p *= h;

        c = cos(p.y * 1.2);
        s = sin(p.x + p.z) * .08 - .02;
        c = abs(c);

        d = .5 + 5. * c * sin(s);
        a += .8 * d / h; 

        if(cos(t * .2) < 0.)
        o += vec4(s * s * c, 3e-5 / d / 2., 3e-5 / d, 0);
   }

   if(sin(t * .2) < 0. || cos(t * .2) > 0.)
   o += 3. * vec4(8, 4, 1, 0) / i + vec4(0, abs(.4*d), 0, 0) * .0;
}
