#define R(a) mix(q*dot(p,q),p,cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-.577);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=.4;
    p=R(iTime);

    for(j=0.;j++<2.;)
        p=abs(abs(p)-.05)-.01,
        p=vec3(p.x*p.x+p.y*p.y, 2.*p.x*p.y, p.z),
        d=max(max(p.z,p.x),p.z),
        d-=dot(p,p)/2e4;
    e+=d;

}o+=vec4(-log(p),1)*3./i;
}
