#define R(a) mix( f*dot(p,f), p, cos(a) ) + sin(a)*cross(p,f)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e,g; vec3 p,f=vec3(-.577);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=1.;
    p=R(g=iTime*.5);

    for(j=0.;j++<4.;){
        p=abs(abs(p=R(2.6-log(iTime)))-cos(g)/16.)-.02,
        d=max(max(p.x,p.y),p.z),
        d-=dot(p,p)/2e4;
    }
    e+=d;
}o+=vec4(atan(-p/2.),1)*i*j;
}
