#define aa 2./iResolution.y

// Hash12 By Dave_Hoskins: https://www.shadertoy.com/view/4djSRW
float hash12(float p)
{
	vec3 p3  = fract(vec3(p) * .1031);
    p3 += dot(p3, p3 + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
//https://www.shadertoy.com/view/lt3BW2
float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}

float sdfCircle(vec2 p, float radius){ 
    return length(p) - radius;
}

float fireField(vec2 p) {
    float field = 1.;
    for(float i = 0.; i < 1.; i += 0.001){
        vec2 offset = vec2(
            hash12(i)*2.-1., 
            (hash12(i+0.1)*20.));
        offset.y -= fract(iTime/8.)*16.;
        field = opSmoothUnion(field, sdfCircle(p+offset, 0.3*offset.y-0.15)*10., 3.);
    }
    field = 1.0-clamp(field,0.,1.);
    return pow(field,4.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - iResolution.xy*.5)/iResolution.y; // normalize space

    float gradient = fireField(p);

    // Create fire color gradient
    float middleOffset = 0.03;
    vec3 firstColor = vec3(0.,0.,0.);
    vec3 middleColor = vec3(1.,.3,0.0);
    vec3 endColor = vec3(0.,0.,0.);
    vec3 col = mix(
        mix(firstColor, middleColor, gradient/middleOffset), 
        mix(middleColor, endColor, (gradient - middleOffset)/(1.0 - middleOffset)), 
        step(middleOffset, gradient)
    );

    float v = step(1.,(p.x*10./2.)-1.);

    fragColor = vec4(col,1.0);
}
