void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    float aspect = iResolution.x / iResolution.y;
    vec2 center_relative = vec2(0.5 * aspect, 0.5);

    float dis = distance(center_relative, uv);

    vec4 res = vec4(1.0);

    float inc = iTime * 0.03;

    if (mod(inc, dis) < 0.05) {
        res.g = 0.0;
    }

    // Output to screen
    fragColor = res;
}
