vec2 function(vec2 z, vec2 c)
{
    return mat2(z, -z.y, z.x)*z + c;
}

float cC(float currentC, float colorToChange) // ChangeColor
{
    if (colorToChange != -1.) return colorToChange;
    return currentC;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float range = 2.5;
    float didAt = texelFetch( iChannel0, ivec2(fragCoord.x,fragCoord.y), 0).z;

    vec2 preZ = texelFetch( iChannel0, ivec2(fragCoord.x,fragCoord.y), 0).xy;
    if (didAt == 0.) {
        vec2 uv = fragCoord / iResolution.xy;
        uv -= vec2(.6, .5);
        uv.x *= iResolution.x/iResolution.y;
        uv *= range;

        vec2 z = function(preZ, uv);
        if (length(z) >= 2.) fragColor = vec4(0., 0., float(iFrame), 0); else fragColor = vec4(z, 0., 0);
    } else {
        fragColor = vec4(0, 0, didAt, (didAt)/float(iFrame));
    }
}
