// variant of https://www.shadertoy.com/view/ddf3zn
#define PI 3.14159265359
float stripe(vec2 uv) {
    uv.y += sin(uv.x * 50.0) * 0.01;
	float f = fract(uv.x + uv.y);
	float c = smoothstep(0.25, 0.3, f) * smoothstep(0.8, 0.75, f);
	return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.x;
	float a = smoothstep(0.4, 0.41, uv.x) * smoothstep(0.6, 0.59, uv.x);
	uv = fract((uv - 0.4) / 0.2);
	float theta = acos(1.0 - 2.0*uv.x);
	uv.x = theta/PI + iTime*0.5;
	float c = mix(0.4, 0.7, stripe(uv)) * sin(theta);
	fragColor = vec4(vec3(mix(0.2, c, a)), 1.0);
}
