void mainImage(out vec4 C, vec2 c)
{
    vec2 p = (c - iMouse.xy) / iResolution.x * 7., q;
    p = vec2(p.y, -p.y) + p.x;

    float d = 1e4, e, id = 0.;

    for(int i = 0; i < 4; ++i)
    {
        q = abs(mod(p + vec2(i, i / 2), 2.) - 1.);
        e = max(max(q.x, q.y), (q.x + q.y) / sqrt(2.)) - .85;
        id += sign(e);
        d = min(d, abs(e));
    }

    C = vec4(pow((sin(id * 9. * vec3(1, 2, 3)) * .4 + .6) * smoothstep(.01, .02, abs(d)), vec3(1. / 2.2)), 1.);
}
