#define PI 3.1415926535

#define BLUE vec3(0, 0, 150) / 255.
#define RED vec3(255, 0, 0) / 255.
#define YELLOW vec3(0, 0, 137.) / 255.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 st = fragCoord / iResolution.xy;
	float w = sin((uv.x + uv.y - iTime * .75 + sin(1.5 * uv.x + 4.5 * uv.y) * PI * .3)
                  * PI * .6); // fake waviness factor

    uv *= 1. + (.036 - .036 * w);
    vec3 col = vec3(0.);

    // flag colors
    col += 1.;
    col = mix(col, RED, smoothstep(.35, .36, uv.y));
    col = mix(col, BLUE, smoothstep(-.35, -.36, uv.y));
    col += w * .225;

    float v = 16. * st.x * (1. - st.x) * st.y * (1. - st.y); // vignette
	col *= 1. - .6 * exp2(-1.75 * v);
    fragColor = vec4(col, 1.);
}
