#define X c += texture(iChannel0, p * 0.1 - t * 0.002); p *= 0.4; c *= 0.7;

// Function to generate random values based on position
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 f, vec2 w) {
    vec2  p = iResolution.xy;
    float d = length( p = ( w+w - p ) / p.y ),
          t = iTime;
    vec4  b = vec4(.95,.13,.48,1) + p.y *37.5,
          c = b + b;
    p = p * asin(d) / d + 5.;
    p = p * p.y + t;

    for( int i; i++ < 5 ; p *= .4, c *= .7 )
      c += texture(iChannel0, p*.1 - t/5e2 );

// c += .01 * fract(sin(dot(w, vec2(12.9898, 78.233))) * 1e4 ); // twinkling stars invisble

    f = ( c.g + (b - c.g) * c.r ) * (1.25 - d * d);
}
